package lab01

import kotlin.test.Test
import kotlin.test.*

class AppTest {
    @Test fun testAppHasAGreeting() {
        val classUnderTest = App()
        assertNotNull(classUnderTest.greeting, "app should have a greeting")
    }
    @Test
    fun testPalindromeWord(){
        var s = "aba".isPalindrome()
        assertTrue(message="wrong implementation of the function: please check your function and try again",actual=s)
    }
    @Test
    fun testWrongPalindromeWord(){
        var s = "abaaaa".isPalindrome()
        assertFalse(message="wrong implementation of the function: please check your function and try again",actual=s)
    }
    @Test
    fun testCamelCasePalindromeWord(){
        var s = "Aba".isPalindrome(ignoreCase = true)
        assertTrue(message="wrong implementation of the function: please check your function and try again",actual=s)
    }
    @Test
    fun testCamelCaseAndBlanksPalindromeWord(){
        var s = "Ab     a".isPalindrome(ignoreCase = true, ignoreWhiteSpaces = true)
        assertTrue(message="wrong implementation of the function: please check your function and try again",actual=s)
    }
    @Test
    fun TestCaseWhenStringIsEmpty(){
        var n = "                    ".isPalindrome()
        assertFalse(message="checks whether an if statement includes an empty string or only blanks",actual=n)
    }
    @Test
    fun TestCaseWhenStringIs1(){
        var n = "a".isPalindrome()
        assertTrue(message="check if an if condition has been issued if string length is exactly equal to 1", actual=n)
    }

    }
